import React, {useState} from "react";
import { Editor } from "react-draft-wysiwyg";
import { EditorState } from 'draft-js';
import {CopyToClipboard} from 'react-copy-to-clipboard';
import "react-draft-wysiwyg/dist/react-draft-wysiwyg.css";

function Callouts() {
    const [copyInfo, setCopyInfo]=useState({
        "value":'', 
        "copied":false
    });

 

    const [ categoryType, setCategoryType] =useState(['']);

    
    const handleChange = (e)=>{
        setCategoryType(e.target.value);
        console.clear();
        console.log(categoryType);
    }
  

  return (
    <div className="container ms-4 mt-4">
      <div class="form-floating mb-4">
        <select
          class="form-select"
          id="floatingSelect"
          aria-label="Floating label select example" onChange={(e)=>handleChange(e)}
        >
          <option selected>Choose a Category</option>
          <option value="Teachers">Teachers</option>
          <option value="Students">Students</option>
        </select>
        <label for="floatingSelect">Callouts Type</label>
      </div>
      <div className="editor-div" >
        <Editor />
      </div>
      <input class="btn btn-primary mt-4" type="button" value="Copy"></input>
      <input class="btn btn-outline-dark mt-4 ms-4" type="button" value="Cancel"></input>
    </div>
  );
}

export default Callouts;
