import React, { useState } from "react";
import { Editor } from "react-draft-wysiwyg";
import { EditorState } from "draft-js";
import { CopyToClipboard } from "react-copy-to-clipboard";
import "react-draft-wysiwyg/dist/react-draft-wysiwyg.css";

function Callouts(props) {
  const [copyInfo, setCopyInfo] = useState({
    value: "",
    copied: false,
  });

  const [categoryType, setCategoryType] = useState([
    "Choose Category",
    "Teachers",
    "Students",
  ]);
  const Addcategory = categoryType.map((Addcategory) => Addcategory);
  
  const handleChange = (e) => {
    const flag=0;
    console.clear();
    console.log(categoryType[e.target.value]); // categoryType[0], categoryType[1]
    //console.log(props.message[0].calloutName);
    if (categoryType[e.target.value] === props.message[0].calloutName) {
      //alert("TEACHERS CALLOUT OPTION SELECTED");
      // json -> display
      flag =1;
    } else if (categoryType[e.target.value] === props.message[1].calloutName) {
      //alert("STUDENTS CALLOUT OPTION SELECTED");
      flag =0;
    }
  };

  return (
    <div className="container ms-4 mt-4">
      <div class="form-floating mb-4">
        <select
          class="form-select"
          id="floatingSelect"
          aria-label="Floating label select example"
          onChange={(e) => handleChange(e)}
        >
          {Addcategory.map((type, key) => (
            <option key={key} value={key}>
              {type}
            </option>
          ))}
        </select>
        <label for="floatingSelect">Callouts Type</label>
      </div>
      <div className="editor-div">
        {/* <Editor
        initialValue="<p>Initial content</p>" /> */}
        if(flag === 1)
        {
          <textarea name="editor" id="editor" cols="30" rows="10">
            {props.message[0].calloutMessage}
          </textarea>
        }
        else if(categoryType[e.target.value] === props.message[1].calloutName)
        {
          <textarea name="editor" id="editor" cols="30" rows="10">{props.message[1].calloutMessage}</textarea>
        }
        
      </div>
      <input class="btn btn-primary mt-4" type="button" value="Copy"></input>
      <input
        class="btn btn-outline-dark mt-4 ms-4"
        type="button"
        value="Cancel"
      ></input>
    </div>
  );
}

export default Callouts;
