import React, { useState } from "react";
import { Editor } from "react-draft-wysiwyg";
import { ContentState, convertToRaw } from "draft-js";
import { CopyToClipboard } from "react-copy-to-clipboard";
import "react-draft-wysiwyg/dist/react-draft-wysiwyg.css";

function Callouts(props) {


 //const [flag, setFlag]= useState(0);
 let _contentState="";
 let raw='';
if(flag === 1){
   _contentState = ContentState.createFromText(props.message[2].calloutMessage);
   raw = convertToRaw(_contentState);
   
}else if(flag === 2){
   _contentState = ContentState.createFromText(props.message[2].calloutMessage);
   raw = convertToRaw(_contentState);
}else{
  _contentState = ContentState.createFromText(props.message[0].calloutMessage);
  raw = convertToRaw(_contentState);
}
 
const [contentState, setContentState] = useState(raw);
  const [categoryType, setCategoryType] = useState([
    "Choose Category",
    "Teachers",
    "Students",
  ]);
  const Addcategory = categoryType.map((Addcategory) => Addcategory);

  const handleChange = (e) => {
    console.clear();
    console.log(categoryType[e.target.value]); // categoryType[0], categoryType[1]
    //console.log(props.message[0].calloutName);
    if (categoryType[e.target.value] === props.message[1].calloutName) {
      //alert("TEACHERS CALLOUT OPTION SELECTED");
      // json -> display
      //setFlag(1);
      setContentState(props.message[1].calloutMessage);
      console.log(`Teachers Flag : ${contentState}`);
    } else if (categoryType[e.target.value] === props.message[2].calloutName) {
      //alert("STUDENTS CALLOUT OPTION SELECTED");
      //setFlag(2);
      setContentState(props.message[2].calloutMessage);
      console.log(`Students Flag : ${contentState}`);
    }
  };

 
 
    return (
      <div className="container ms-4 mt-4">
        <div class="form-floating mb-4">
          <select
            class="form-select"
            id="floatingSelect"
            aria-label="Floating label select example"
            onChange={(e) => handleChange(e)}
          >
            {Addcategory.map((type, key) => (
              <option key={key} value={key}>
                {type}
              </option>
            ))}
          </select>
          <label for="floatingSelect">Callouts Type</label>
        </div>
        <div className="editor-div">
        <Editor
        defaultContentState={contentState}
        onEditorStateChange={setContentState}
      />
            {/* <textarea name="editor" id="editor" cols="100" rows="10" value={props.message[0].calloutMessage}>
             
            </textarea> */}
        </div>
        <input class="btn btn-primary mt-4" type="button" value="Copy"></input>
        <input
          class="btn btn-outline-dark mt-4 ms-4"
          type="button"
          value="Cancel"
        ></input>
      </div>
    );
  
  
}

export default Callouts;
