import React, { useState } from "react";
import copy from "copy-to-clipboard";
import "react-draft-wysiwyg/dist/react-draft-wysiwyg.css";

function Callouts(props) {
  const [predefinedCalloutMsg, setPredefinedCalloutMsg] = useState(
    props.message[0].calloutMessage
  );
  const [dialogState, setDialogState] = useState(["show", "hide"]);

  const [categoryType, setCategoryType] = useState([
    "Choose Category",
    "Teachers",
    "Students",
  ]);
  const Addcategory = categoryType.map((Addcategory) => Addcategory);

  const handleChange = (e) => {
    console.clear();
    console.log(categoryType[e.target.value]);
    if (categoryType[e.target.value] === props.message[1].calloutName) {
      setPredefinedCalloutMsg(props.message[1].calloutMessage);
      //console.log(`Teachers Flag : ${editorState}`);
    } else if (categoryType[e.target.value] === props.message[2].calloutName) {
      //let _contentState= ContentState.createFromText(props.message[2].calloutMessage)
      setPredefinedCalloutMsg(props.message[2].calloutMessage);
      //console.log(`Students Flag : ${editorState}`);
    }
  };
  const editorChange = (e) => {
    console.log("changing..");
  };

